package Chap1_Practice01_InClass;

public class _02_VariablePrint {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int number1 = 100, number2 = 200;
		int total = number1 + number2;

		System.out.print("Result = " + total);
		System.out.print(number1 + " + " + number2 + " = " + total);
		System.out.print("\n");
		
		System.out.println("Result = " + total);
		System.out.println(number1 + " + " + number2 + " = " + total);
		

	}

}
